import React from 'react';
import { useEffect, useState} from 'react';

const request = async () => {
  const reqOps = {
      method: 'GET',
      headers: { 'Content-Type': 'application/json' }
  };
  const response = await fetch(`http://localhost:5000`, reqOps)
  const json = await response.json()
  return json
}

function App() {
  const [data, setData] = useState([])

  const getRecords = async() => {
    let records = await request()
    setData(records)
  }  

  useEffect(() => {
    getRecords()        
  },[data])  


  return (
    <table className="table">
      <thead>
        <tr>
          <th>Nombre</th>
          <th>Apellido</th>
          <th>Edad</th>          
        </tr>
      </thead>
      <tbody>        
          {
            data.map(item => {
              <tr>          
              <td>{item.Nombre}</td>
              <td>{item.Apellidos}</td>
              <td>{item.Edad}</td>
              </tr>
            })
          }
      </tbody>
  </table>
  );
}

export default App;
